sp_widget.script:
(function() {
  /* populate the 'data' object */
  /* e.g., data.table = $sp.getValue('table'); */
  data.pets=[];
  var record = new GlideRecord('x_652573_petsadopt_pets');
  //record.addQuery('user', g_user.userID);
  record.query();
  while (record.next()) {
    var obj={};
    obj.number=record.number.toString();
    obj.name=record.name.toString();
    obj.photo=record.photo.getDisplayValue().toString();
    obj.bio=record.bio.toString();
		obj.id=record.sys_id.toString();
    data.pets.push(obj);

  }

})();
