sp_widget.script:
(function () {
  /* populate the 'data' object */
  /* e.g., data.table = $sp.getValue('table'); */

  var record = new GlideRecord('x_652573_petsadopt_adoptions');
  record.addQuery('user', gs.getUserID());
  // record.addQuery('number',input.selectedPet.number)
  record.query();
  if (record.next()) {
    data.currentPet = {
      id: record.sys_id.toString(),
      photo: record.pet.photo.getDisplayValue().toString(),
      name: record.pet.name.toString()

    };

  }
  gs.info('datafinal=>' + JSON.stringify(data.currentPet));

  if (input && input.selectedPet != data.currentPet) {
    gs.info('dato input==>'+JSON.stringify(input.selectedPet));
    if (data.currentPet) {
       record.pet = input.selectedPet.id;
      //record.pet= input.selectedPet.name;
      record.user= gs.getUserID();
      record.update();
     // data.currentPet = input.selectedPet
     data.currentPet = {
      id: record.sys_id.toString(),
     photo: input.selectedPet.photo.toString(),
      name: input.selectedPet.name.toString()

    };
     gs.info('currentPet=>' + JSON.stringify(data.currentPet));
    } else {
      gs.info('dato3=>' + JSON.stringify(input));
      var newPet = new GlideRecord('x_652573_petsadopt_adoptions');
      //newPet.get('sys_id', data.currentPet.id)

    //  if (!gs.nil(newPet.get(input.selectedPet.id))) {
        gs.info("inicia la inserccion2=>");
        newPet.initialize();
        newPet.user = gs.getUserID();
        newPet.pet = input.selectedPet.id;
        newPet.insert();
      //}
      data.currentPet = input.selectedPet;
    }
  }
})();
